javascript.initialization:
var table = $('#grdResults').DataTable({
  processing: true,
  serverSide: true,
  ajax: {
    url: '/api/boatfuelprices/search',
    type: 'POST',
    data: function(d) {
      d.boatID = $('#cboBoat').val();
      d.locationID = $('#cboLocation').val();
      d.fuelType = $('#cboFuelType').val();
      d.startDate = $('#dtpStartDate').val();
      d.endDate = $('#dtpEndDate').val();
      d.minPrice = $('#txtMinPrice').val();
      d.maxPrice = $('#txtMaxPrice').val();
      d.vendorID = $('#cboVendor').val();
      d.activeOnly = $('#chkActiveOnly').is(':checked');
    }
  },
  columns: [
    { data: 'boatName', name: 'BoatName' },
    { data: 'locationName', name: 'LocationName' },
    { data: 'fuelType', name: 'FuelType' },
    { data: 'priceDate', name: 'PriceDate', render: function(data) { return moment(data).format('MM/DD/YYYY'); } },
    { data: 'pricePerGallon', name: 'PricePerGallon', className: 'text-right', render: function(data) { return '$' + parseFloat(data).toFixed(4); } },
    { data: 'quantity', name: 'Quantity', className: 'text-right', render: function(data) { return data ? parseFloat(data).toFixed(2) : ''; } },
    { data: 'totalCost', name: 'TotalCost', className: 'text-right', render: function(data) { return data ? '$' + parseFloat(data).toFixed(2) : ''; } },
    { data: 'vendorName', name: 'VendorName' },
    { data: 'invoiceNumber', name: 'InvoiceNumber' },
    { data: 'notes', name: 'Notes' },
    { data: 'isActive', name: 'IsActive', render: function(data) { return data ? '<input type="checkbox" checked disabled />' : '<input type="checkbox" disabled />'; } },
    {
      data: 'boatFuelPriceID',
      orderable: false,
      render: function(data, type, row) {
        var actions = '<div class="btn-group btn-group-sm">';
        actions += '<button class="btn btn-info btn-view" data-id="' + data + '" title="View"><i class="fas fa-eye"></i></button>';
        if (hasModifyPermission) {
          actions += '<button class="btn btn-warning btn-edit" data-id="' + data + '" title="Edit"><i class="fas fa-edit"></i></button>';
          actions += '<button class="btn btn-danger btn-delete" data-id="' + data + '" title="Delete"><i class="fas fa-trash"></i></button>';
        }
        actions += '</div>';
        return actions;
      }
    }
  ],
  order: [[3, 'desc']],
  pageLength: 25,
  responsive: true,
  dom: 'Bfrtip',
  buttons: ['copy', 'csv', 'excel', 'pdf', 'print']
});
